export const operationReducer = (state, action) => {
    switch (action.type) {
        case "ADD":
            return '+';
        case "SUBTRACT":
            return '-';
        case "MULTIPLY":
            return '*'
        case "DIVIDE":
            return '/';
        default:
            return state;
    }
}
