const displayValueReducer = (state = 0, action) => {
    switch (action.type) {
        case "ADD":
            return action.value;
        case "SUBTRACT":
            return action.value;
        case "MULTIPLY":
            return action.value;
        case "DIVIDE":
            return action.value;
        default:
            return state;
    }
}

export default displayValueReducer;
