export const rootReducer = (state, action) => {
    switch (action.type) {
        case "NUM":
            return state.result ? 
                {...state, firstVal: action.value, result: false} : 
                state.isFirst ? 
                    {...state, firstVal: (state.firstVal * 10) + action.value} : 
                    {...state, secondVal: (state.secondVal * 10) + action.value}
        case "OP":
            return {...state, op: action.value, isFirst: !state.isFirst}
        case "CLEAR":
            return {firstVal: 0, op: null, secondVal: 0, isFirst: true}
        case "EQUALS":
            switch (state.op) {
                case '+':
                    return {firstVal: state.firstVal + state.secondVal, secondVal: 0, op: null, isFirst: true, result: true}
                case '-':
                    return {firstVal: state.firstVal - state.secondVal, secondVal: 0, op: null, isFirst: true, result: true}
                case '*':
                    return {firstVal: state.firstVal * state.secondVal, secondVal: 0, op: null, isFirst: true, result: true}
                case '/':
                    return {firstVal: state.firstVal / state.secondVal, secondVal: 0, op: null, isFirst: true, result: true}
                default:
                    return {...state, op: null, isFirst: true}
            }
        default:
            return {...state}
    }
}
